    public static class BlurHelper{
        
        public static int max_width = 25;
        public static int limitWidth(int width){
            if (width % 2 == 0){
                width += 1;
            }
            width = Math.Min(width, max_width);
            return Math.Max(3, width);
        }
    }

    public static class SmoothBlur {
        public static List<float[]> GetKernel(int width){
            float[] vals = new float[width];
            Helpers.Fill<float>(vals, width, 1f / width);
            return new List<float[]>() {vals, vals};
        }
    }
